  data: {
    motto: 'Hello World!',
    userInfo: {},
  },
  onLoad() {
    // 异步触发1次，最终态Hello Vedux!
    this.fetchAPI({motto: 'Hello Vedux!'})
  },
};

const mapStateToData = ({user, home}) => {
  return {
    userInfo: user.userInfo,
    motto: home.motto,
  };
};

